using System;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;using System.Linq;using System.Text;using System.Text.Json.Serialization;using System.Threading.Tasks;namespace BlazorSamples.Shared.Twilio.GrpcAudioStream.Start{    public record struct InboundStart    {        public const string ACCOUNT_SID = "accountSid";        public const string STREAM_SID = "streamSid";        public const string CALL_SID = "callSid";        public const string TRACKS = "tracks";        public const string MEDIA_FORMAT = "mediaFormat";        public const string CUSTOM_PARAMETERS = "customParameters";        [Required(AllowEmptyStrings = false)]        [JsonPropertyOrder(1)]        [JsonPropertyName(ACCOUNT_SID)]        public required string AccountSid { get; init; }        [Required(AllowEmptyStrings = false)]        [JsonPropertyOrder(2)]        [JsonPropertyName(STREAM_SID)]        public required string StreamSid { get; init; }        [Required(AllowEmptyStrings = false)]        [JsonPropertyOrder(3)]        [JsonPropertyName(CALL_SID)]        public required string CallSid { get; init; }        [Required]        [JsonPropertyOrder(4)]        [JsonPropertyName(TRACKS)]        public required string[] Tracks { get; init; }        [Required]        [JsonPropertyOrder(5)]        [JsonPropertyName(MEDIA_FORMAT)]        public InboundMediaFormat MediaFormat { get; init; }        [Required]        [JsonPropertyOrder(6)]        [JsonPropertyName(CUSTOM_PARAMETERS)]        public InboundCustomParameters CustomParameters { get; init; }        public InboundStart()        {            MediaFormat = new InboundMediaFormat();            CustomParameters = new InboundCustomParameters();        }    }}